package com.wip.training.exercise3;

public class SwapInteger {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int x = 5;
        int y = 10;
        int a;

        a = x;
        x = y;
        y = a;

        System.out.println("value of x = " + x + "\nvalue of y = " + y);

    }

}
